import React,{ useState, useEffect } from 'react'
import axios from 'axios'

const FormProduct = () => {

  const [products, setProducts] = useState([])
  const [form, setForm] = useState([])

  useEffect(() => {
    loadData()
  }, [])

  const loadData = async () => {
      await axios.get('http://localhost:5000/api/product')
        .then((response) => setProducts(response.data))
        .catch((error) => console.log(error))
  }

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    await axios.post('http://localhost:5000/api/product', form)
  }

  return (
    <div>
      FormProject

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Name</label>
          <input type="text" className="form-control" id="name" name='name' placeholder='name' onChange={e => handleChange(e)}/>
        </div>
        <div className="mb-3">
          <label htmlFor="detail" className="form-label">Detail</label>
          <input type="text" className="form-control" id="detail" name='detail' placeholder='detail' onChange={e => handleChange(e)}/>
        </div>
        <div className="mb-3">
          <label htmlFor="price" className="form-label">Price</label>
          <input type="text" className="form-control" id="price" name='price' placeholder='price' onChange={e => handleChange(e)}/>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>


      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Detail</th>
            <th scope="col">Price</th>
          </tr>
        </thead>
        <tbody>
          {
            products ? products.map((product, index) => {
              return (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{product.name}</td>
                  <td>{product.detail}</td>
                  <td>{product.price}</td>
                </tr>
              )
            }) : null
          }
        </tbody>
      </table>
    </div>
  )
}

export default FormProduct
